import { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";
const NewExpense = (props) => {
  const expenseSaveDataHandler = (enteredExpenseData) => {
    const expenseData = { ...enteredExpenseData, id: Math.random().toString() };
    props.onAddExpense(expenseData);
  };

  //cancel button handler
  const onCancelHandler = () => {
    setNewExpContent(cancelled);
  };
  // new expense handler
  const newExpenseHandler = () => {
    setNewExpContent(
      <ExpenseForm
        onSaveExpenseData={expenseSaveDataHandler}
        onCancelHandler={onCancelHandler}
      />
    );
  };
  const cancelled = (
    <button onClick={newExpenseHandler}>Add New Expense</button>
  );
  const [newExpContent, setNewExpContent] = useState(cancelled);

  return <div className="new-expense">{newExpContent}</div>;
};
export default NewExpense;
